import { useState } from "react";
import {
  Lock,
  Trash2,
  ShieldCheck,
  AlertTriangle,
} from "lucide-react";

const API_BASE_URL = process.env.REACT_APP_API_URL;

export default function Account({
  studentId,
  account,
  token,
  onAccountDeleted,
}) {
  const [deleting, setDeleting] = useState(false);

  /* ================= DELETE ACCOUNT ================= */
  const deleteAccount = async () => {
    const confirm = window.confirm(
      "Are you sure you want to delete this account? This action cannot be undone."
    );
    if (!confirm) return;

    setDeleting(true);
    try {
      const res = await fetch(
        `${API_BASE_URL}/api/v1/admin/student-users/${studentId}`,
        {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!res.ok) throw new Error();
      alert("✅ Account deleted successfully");
      onAccountDeleted?.();
    } catch {
      alert("❌ Failed to delete account");
    } finally {
      setDeleting(false);
    }
  };

  /* ================= UI ================= */
  return (
    <div className="card">
      <div className="card-header">
        <div>
          <h3 className="card-title">
            <ShieldCheck size={18} /> Account Information
          </h3>
          <p className="card-subtitle">
            Student login and access details
          </p>
        </div>
      </div>

      <div className="card-body">
        <div className="account-info">
          <div>
            <label>Email / Login ID</label>
            <p>{account?.login_id}</p>
          </div>

          <div>
            <label>Status</label>
            <span
              className={`badge ${
                account?.usr_active ? "badge-success" : "badge-danger"
              }`}
            >
              {account?.usr_active ? "Active" : "Inactive"}
            </span>
          </div>
        </div>
      </div>

      <div className="card-footer d-flex justify-between">
        <span className="text-muted text-sm">
          Account actions
        </span>

        <button
          className="btn btn-danger btn-sm"
          onClick={deleteAccount}
          disabled={deleting}
        >
          <Trash2 size={16} />
          {deleting ? "Deleting…" : "Delete Account"}
        </button>
      </div>
    </div>
  );
}

/* ================= PASSWORD RESET ================= */
Account.Password = function PasswordTab({ studentId, token }) {
  const [password, setPassword] = useState({
    new: "",
    confirm: "",
  });
  const [saving, setSaving] = useState(false);

  const resetPassword = async () => {
    if (!password.new || password.new.length < 6) {
      alert("Password must be at least 6 characters");
      return;
    }

    if (password.new !== password.confirm) {
      alert("Passwords do not match");
      return;
    }

    setSaving(true);
    try {
      const res = await fetch(
        `${API_BASE_URL}/api/v1/admin/student-users/${studentId}/password`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ password: password.new }),
        }
      );

      if (!res.ok) throw new Error();
      alert("✅ Password reset successful");
      setPassword({ new: "", confirm: "" });
    } catch {
      alert("❌ Failed to reset password");
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="card">
      <div className="card-header">
        <div>
          <h3 className="card-title">
            <Lock size={18} /> Reset Password
          </h3>
          <p className="card-subtitle">
            Set a new password for this account
          </p>
        </div>
      </div>

      <div className="card-body">
        <div className="form-group">
          <label className="form-label">New Password</label>
          <input
            type="password"
            className="form-input"
            value={password.new}
            onChange={(e) =>
              setPassword({ ...password, new: e.target.value })
            }
          />
        </div>

        <div className="form-group">
          <label className="form-label">Confirm Password</label>
          <input
            type="password"
            className="form-input"
            value={password.confirm}
            onChange={(e) =>
              setPassword({
                ...password,
                confirm: e.target.value,
              })
            }
          />
        </div>

        <div className="alert alert-warning">
          <AlertTriangle size={18} />
          <div>
            <strong>Security Notice</strong>
            <p>
              Resetting the password will immediately invalidate the old one.
            </p>
          </div>
        </div>
      </div>

      <div className="card-footer">
        <button
          className="btn btn-primary"
          onClick={resetPassword}
          disabled={saving}
        >
          <Lock size={16} />
          {saving ? "Resetting…" : "Reset Password"}
        </button>
      </div>
    </div>
  );
};
