import React, { useEffect, useState, useCallback } from "react";
import { Search, User, PlusCircle, X } from "lucide-react";
import Profile from "./Students/Profile";
import Account from "./Students/Account";
import "./user-management.css";

const API_BASE_URL = process.env.REACT_APP_API_URL;
const TABS = ["search", "profile", "account", "password"];

const UserManagement = ({ token }) => {
  const [tab, setTab] = useState("search");
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [selectedStudentID, setSelectedStudentID] = useState(null);

  const [accountExists, setAccountExists] = useState(false);
  const [checkingAccount, setCheckingAccount] = useState(false);

  const [showCreateForm, setShowCreateForm] = useState(false);
  const [creating, setCreating] = useState(false);

  const [accountForm, setAccountForm] = useState({
    loginID: "",
    usr_active: true,
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [searched, setSearched] = useState(false);

  /* =========================
     SEARCH STUDENT
  ========================= */
  const searchStudents = async () => {
    const q = query.trim();
    if (!q) return;

    setLoading(true);
    setError("");
    setResults([]);
    setSearched(false);

    try {
      const res = await fetch(
        `${API_BASE_URL}/api/v1/admin/students/${q}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (!res.ok) throw new Error();

      const data = await res.json();
      setResults(Array.isArray(data) ? data : [data]);
      setSearched(true);
    } catch {
      setError("Unable to search students");
      setResults([]);
      setSearched(true);
    } finally {
      setLoading(false);
    }
  };

  /* =========================
     SELECT STUDENT
  ========================= */
  const selectStudent = useCallback((student) => {
    if (!student?.student_id) return;

    setSelectedStudentID(student.student_id);
    setTab("profile");

    // üîë Reset states on student change
    setAccountExists(false);
    setShowCreateForm(false);
  }, []);

  /* =========================
     CHECK ACCOUNT EXISTENCE
  ========================= */
  useEffect(() => {
    if (!selectedStudentID) return;

    const checkAccount = async () => {
      setCheckingAccount(true);
      try {
        const res = await fetch(
          `${API_BASE_URL}/api/v1/admin/student-users/${selectedStudentID}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setAccountExists(res.ok);
      } catch {
        setAccountExists(false);
      } finally {
        setCheckingAccount(false);
      }
    };

    checkAccount();
  }, [selectedStudentID, token]);

  /* =========================
     CREATE ACCOUNT
  ========================= */
  const submitCreateAccount = async (e) => {
    e.preventDefault();

    if (!accountForm.loginID.trim()) {
      alert("Login ID is required");
      return;
    }

    setCreating(true);
    try {
      const res = await fetch(
        `${API_BASE_URL}/api/v1/admin/student-users/${selectedStudentID}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(accountForm),
        }
      );

      if (!res.ok) throw new Error();

      setAccountExists(true);
      setShowCreateForm(false);
      setTab("account");
      alert("‚úÖ Account created successfully");
    } catch {
      alert("‚ùå Failed to create account");
    } finally {
      setCreating(false);
    }
  };

  /* =========================
     RENDER
  ========================= */
  return (
    <div className="profile-page">
      <div className="profile-header">
        <h2>User Management</h2>
        <p>Search and manage student user accounts</p>
      </div>

      {/* Tabs */}
      <div className="tabs">
        {TABS.map((t) => (
          <button
            key={t}
            className={`tab ${tab === t ? "active" : ""}`}
            onClick={() => setTab(t)}
            disabled={
              t !== "search" &&
              (!selectedStudentID ||
                (t === "password" && !accountExists))
            }
          >
            {t.toUpperCase()}
          </button>
        ))}
      </div>

      {/* SEARCH */}
      {tab === "search" && (
        <>
          <div className="search-box">
            <input
              placeholder="Student ID"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && searchStudents()}
            />
            <button onClick={searchStudents}>
              <Search size={18} />
            </button>
          </div>

          {loading && <div className="profile-state loading">Searching‚Ä¶</div>}
          {error && <div className="profile-state error">{error}</div>}
          {!loading && searched && results.length === 0 && (
            <div className="profile-state empty">No students found.</div>
          )}

          {results.map((s) => (
            <div
              key={s.student_id}
              className="search-result"
              onClick={() => selectStudent(s)}
            >
              <User size={18} />
              <div>
                <strong>{s.per_name}</strong>
                <div className="muted">{s.student_id}</div>
              </div>
            </div>
          ))}
        </>
      )}

      {/* PROFILE */}
      {tab === "profile" && selectedStudentID && (
        <>
          <Profile studentId={selectedStudentID} token={token} />

          {!checkingAccount && !accountExists && (
            <div className="profile-card warning">
              <p>‚ö†Ô∏è No account exists for this student.</p>
              <button
                className="btn-primary"
                onClick={() => setShowCreateForm(true)}
              >
                <PlusCircle size={16} /> Create Account
              </button>
            </div>
          )}

          {/* ‚úÖ CREATE ACCOUNT FORM (FIXED) */}
          {showCreateForm && (
            <div className="profile-card">
              <div className="card-header">
                <h3>Create Student Account</h3>
                <button
                  className="icon-btn"
                  onClick={() => setShowCreateForm(false)}
                >
                  <X size={18} />
                </button>
              </div>

              <form onSubmit={submitCreateAccount}>
                <label>Login ID</label>
                <input
                  type="text"
                  value={accountForm.loginID}
                  onChange={(e) =>
                    setAccountForm({
                      ...accountForm,
                      loginID: e.target.value,
                    })
                  }
                  required
                />

                <label className="checkbox">
                  <input
                    type="checkbox"
                    checked={accountForm.usr_active}
                    onChange={(e) =>
                      setAccountForm({
                        ...accountForm,
                        usr_active: e.target.checked,
                      })
                    }
                  />
                  Active Account
                </label>

                <button className="btn-primary" disabled={creating}>
                  {creating ? "Creating..." : "Create Account"}
                </button>
              </form>
            </div>
          )}
        </>
      )}

      {/* ACCOUNT */}
      {tab === "account" && selectedStudentID && accountExists && (
        <Account studentId={selectedStudentID} token={token} />
      )}

      {/* PASSWORD */}
      {tab === "password" && selectedStudentID && accountExists && (
        <Account.Password studentId={selectedStudentID} token={token} />
      )}
    </div>
  );
};

export default UserManagement;
